/**
 * Represents the Game Jolt server time.
 */
export interface Time {
    /**
     * The UNIX time stamp (in seconds) representing the server's time.
     */
    timestamp: number;
    /**
     * The timezone of the server.
     */
    timezone: string;
    /**
     * The current year.
     */
    year: number;
    /**
     * The current month.
     */
    month: number;
    /**
     * The day of the month.
     */
    day: number;
    /**
     * The hour of the day.
     */
    hour: number;
    /**
     * The minute of the hour.
     */
    minute: number;
    /**
     * The seconds of the minute.
     */
    second: number;
}
